-- Dados da tabela domains
INSERT INTO domains VALUES (1, 'Gestão da Integração', 'Processos e atividades necessários para identificar, definir, combinar, unificar e coordenar os vários processos e atividades do gerenciamento de projetos.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (2, 'Gestão do Escopo', 'Processos necessários para garantir que o projeto inclua todo o trabalho necessário, e apenas ele, para completar o projeto com sucesso.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (3, 'Gestão do Tempo', 'Processos necessários para gerenciar o término do projeto no prazo.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (4, 'Gestão do Custo', 'Processos envolvidos em planejamento, estimativa, orçamentação, financiamento, gerenciamento e controle dos custos.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (5, 'Gestão da Qualidade', 'Processos e atividades da organização executora que determinam as políticas de qualidade, os objetivos e as responsabilidades.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (6, 'Gestão de Recursos', 'Processos que organizam, gerenciam e lideram a equipe do projeto.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (7, 'Gestão das Comunicações', 'Processos necessários para garantir que as informações do projeto sejam geradas, coletadas, distribuídas, armazenadas, recuperadas e organizadas de maneira oportuna e apropriada.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (8, 'Gestão dos Riscos', 'Processos de condução do planejamento do gerenciamento de riscos, identificação, análise, planejamento de respostas e monitoramento e controle dos riscos do projeto.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (9, 'Gestão das Aquisições', 'Processos necessários para comprar ou adquirir produtos, serviços ou resultados necessários de fora da equipe do projeto.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');
INSERT INTO domains VALUES (10, 'Gestão de Stakeholders', 'Processos necessários para identificar as pessoas, grupos ou organizações que podem impactar ou ser impactados pelo projeto.', '2025-05-14 01:13:36', '2025-05-14 01:13:36');


